import React,{Component} from "react"
import $ from "jquery"
import "font-awesome/"
class belanja extends Component{
constructor(){
    super()
    this.state={
        barang:[
             {
                 nama:"rinso",
                 harga:5000,
                 jumlah:0,
                 total:0
             }
            ,{
                nama:"baygon",
                harga:10000,
                jumlah:0,
                total:0
            }
            ,{ 
            nama:"susu beruang",
            harga:8000,
            jumlah:0,
            total:0}
        ],
        nama:"",
        harga:"",
        jumlah:"",
        total:"",
        action:""
    }
    
}

render(){
    return(
        <div className="container">
            <ul className="list-group mt-4 d-flex justify-content-start flex-row">
                {
                this.state.barang.map((item,i)=>{
              
                    return(
                        <div className="card mr-5" style={{width:"18rem"}} >
                             <div className="card-header bg-info text-light">
                              keranjang
                          </div>
                          <div className="card-body">
                          <li className="list-group-item d-flex flex-column align-items-start" key={i}>
                         
                         <h5 className="text-primary">{item.nama}</h5>
                         <h6>
                             harga:{item.harga}
                         </h6>
                         <div className="d-flex flex-row">
                         <button><i className=""></i></button>
                         <h6 id="a">{item.jumlah}</h6>
                         <button className="btn btn-success text-light"><i className="fas fa-plus"></i></button>
                
                         </div>
                  <h6>total:{item.total}</h6>
                     </li>
                          </div>
                            
                        </div>
                       
                    )

                })}

            </ul>
        </div>
    )
}
}
export default belanja
