import React,{Component} from "react"
import "fontawesome"
class belanja extends Component{
constructor(){
    super()
    this.state={
        barang:[
             {
                 nama:"rinso",
                 harga:5000,
                 jumlah:0,
                 total:0
             }
            ,{
                nama:"baygon",
                harga:10000,
                jumlah:0,
                total:0
            }
            ,{ 
            nama:"susu beruang",
            harga:8000,
            jumlah:0,
            total:0}
        ],
        nama:"",
        harga:"",
        jumlah:"",
        total:"",
        action:""
    }
}
render(){
    return(
        <div className="container">
            <ul className="list-group mt-4">
                {this.state.barang.map((item,i)=>{
                    return(
                        <li className="list-group-item" key={i}>
                            <h5 className="text-primary">{item.nama}</h5>
                            <h6>
                                harga:{item.harga}
                            </h6>
                    <h6 if>jumlah:{item.jumlah}</h6>
                    <h6>total:{item.total}</h6>

                        </li>
                    )
                })}
            </ul>
        </div>
    )
}
}
export default belanja
